// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract EligibilityVerification {
    struct EligibilityCriteria {
        uint256 minAge;
        uint256 maxAge;
        uint256 minIncome;
        uint256 maxIncome;
        bool isRural;
        bool isBPL;
        bool isFarmer;
        bool isWoman;
        bool isSeniorCitizen;
        bool isDisabled;
    }

    struct UserProfile {
        uint256 age;
        uint256 income;
        bool isRural;
        bool isBPL;
        bool isFarmer;
        bool isWoman;
        bool isSeniorCitizen;
        bool isDisabled;
        bool isVerified;
    }

    mapping(address => UserProfile) public userProfiles;
    mapping(bytes32 => EligibilityCriteria) public schemeCriteria;

    event ProfileCreated(address indexed user);
    event ProfileUpdated(address indexed user);
    event ProfileVerified(address indexed user, address indexed verifier);
    event CriteriaSet(bytes32 indexed schemeId);

    function createProfile(
        uint256 _age,
        uint256 _income,
        bool _isRural,
        bool _isBPL,
        bool _isFarmer,
        bool _isWoman,
        bool _isSeniorCitizen,
        bool _isDisabled
    ) external {
        require(userProfiles[msg.sender].age == 0, "Profile already exists");
        
        userProfiles[msg.sender] = UserProfile({
            age: _age,
            income: _income,
            isRural: _isRural,
            isBPL: _isBPL,
            isFarmer: _isFarmer,
            isWoman: _isWoman,
            isSeniorCitizen: _isSeniorCitizen,
            isDisabled: _isDisabled,
            isVerified: false
        });

        emit ProfileCreated(msg.sender);
    }

    function updateProfile(
        uint256 _age,
        uint256 _income,
        bool _isRural,
        bool _isBPL,
        bool _isFarmer,
        bool _isWoman,
        bool _isSeniorCitizen,
        bool _isDisabled
    ) external {
        require(userProfiles[msg.sender].age != 0, "Profile does not exist");
        
        userProfiles[msg.sender] = UserProfile({
            age: _age,
            income: _income,
            isRural: _isRural,
            isBPL: _isBPL,
            isFarmer: _isFarmer,
            isWoman: _isWoman,
            isSeniorCitizen: _isSeniorCitizen,
            isDisabled: _isDisabled,
            isVerified: false
        });

        emit ProfileUpdated(msg.sender);
    }

    function verifyProfile(address _user) external {
        require(userProfiles[_user].age != 0, "Profile does not exist");
        require(!userProfiles[_user].isVerified, "Profile already verified");
        
        userProfiles[_user].isVerified = true;
        emit ProfileVerified(_user, msg.sender);
    }

    function setSchemeCriteria(
        bytes32 _schemeId,
        uint256 _minAge,
        uint256 _maxAge,
        uint256 _minIncome,
        uint256 _maxIncome,
        bool _isRural,
        bool _isBPL,
        bool _isFarmer,
        bool _isWoman,
        bool _isSeniorCitizen,
        bool _isDisabled
    ) external {
        schemeCriteria[_schemeId] = EligibilityCriteria({
            minAge: _minAge,
            maxAge: _maxAge,
            minIncome: _minIncome,
            maxIncome: _maxIncome,
            isRural: _isRural,
            isBPL: _isBPL,
            isFarmer: _isFarmer,
            isWoman: _isWoman,
            isSeniorCitizen: _isSeniorCitizen,
            isDisabled: _isDisabled
        });

        emit CriteriaSet(_schemeId);
    }

    function checkEligibility(bytes32 _schemeId, address _user)
        external
        view
        returns (bool)
    {
        require(userProfiles[_user].age != 0, "Profile does not exist");
        require(userProfiles[_user].isVerified, "Profile not verified");

        UserProfile memory profile = userProfiles[_user];
        EligibilityCriteria memory criteria = schemeCriteria[_schemeId];

        if (profile.age < criteria.minAge || profile.age > criteria.maxAge) return false;
        if (profile.income < criteria.minIncome || profile.income > criteria.maxIncome) return false;
        if (criteria.isRural && !profile.isRural) return false;
        if (criteria.isBPL && !profile.isBPL) return false;
        if (criteria.isFarmer && !profile.isFarmer) return false;
        if (criteria.isWoman && !profile.isWoman) return false;
        if (criteria.isSeniorCitizen && !profile.isSeniorCitizen) return false;
        if (criteria.isDisabled && !profile.isDisabled) return false;

        return true;
    }

    function getProfile(address _user)
        external
        view
        returns (
            uint256 age,
            uint256 income,
            bool isRural,
            bool isBPL,
            bool isFarmer,
            bool isWoman,
            bool isSeniorCitizen,
            bool isDisabled,
            bool isVerified
        )
    {
        UserProfile memory profile = userProfiles[_user];
        return (
            profile.age,
            profile.income,
            profile.isRural,
            profile.isBPL,
            profile.isFarmer,
            profile.isWoman,
            profile.isSeniorCitizen,
            profile.isDisabled,
            profile.isVerified
        );
    }
}

contracts/SchemeRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract SchemeRegistry {
    struct Scheme {
        string name;
        string description;
        address ministry;
        bool isActive;
        uint256 maxBeneficiaries;
        uint256 currentBeneficiaries;
        mapping(address => bool) beneficiaries;
    }

    struct Ministry {
        string name;
        address ministryAddress;
        bool isActive;
    }

    mapping(bytes32 => Scheme) public schemes;
    mapping(address => Ministry) public ministries;
    mapping(address => bytes32[]) public userApplications;

    event SchemeCreated(bytes32 indexed schemeId, string name, address ministry);
    event MinistryRegistered(address indexed ministryAddress, string name);
    event ApplicationSubmitted(bytes32 indexed schemeId, address indexed applicant);
    event ApplicationApproved(bytes32 indexed schemeId, address indexed applicant);
    event ApplicationRejected(bytes32 indexed schemeId, address indexed applicant);

    modifier onlyMinistry() {
        require(ministries[msg.sender].isActive, "Not a registered ministry");
        _;
    }

    function registerMinistry(string memory _name) external {
        require(!ministries[msg.sender].isActive, "Ministry already registered");
        ministries[msg.sender] = Ministry({
            name: _name,
            ministryAddress: msg.sender,
            isActive: true
        });
        emit MinistryRegistered(msg.sender, _name);
    }

    function createScheme(
        string memory _name,
        string memory _description,
        uint256 _maxBeneficiaries
    ) external onlyMinistry {
        bytes32 schemeId = keccak256(abi.encodePacked(_name, block.timestamp));
        schemes[schemeId].name = _name;
        schemes[schemeId].description = _description;
        schemes[schemeId].ministry = msg.sender;
        schemes[schemeId].isActive = true;
        schemes[schemeId].maxBeneficiaries = _maxBeneficiaries;
        schemes[schemeId].currentBeneficiaries = 0;

        emit SchemeCreated(schemeId, _name, msg.sender);
    }

    function applyForScheme(bytes32 _schemeId) external {
        require(schemes[_schemeId].isActive, "Scheme is not active");
        require(!schemes[_schemeId].beneficiaries[msg.sender], "Already applied");
        require(
            schemes[_schemeId].currentBeneficiaries < schemes[_schemeId].maxBeneficiaries,
            "Scheme is full"
        );

        userApplications[msg.sender].push(_schemeId);
        emit ApplicationSubmitted(_schemeId, msg.sender);
    }

    function approveApplication(bytes32 _schemeId, address _applicant) external onlyMinistry {
        require(schemes[_schemeId].ministry == msg.sender, "Not the scheme ministry");
        require(!schemes[_schemeId].beneficiaries[_applicant], "Already approved");
        require(
            schemes[_schemeId].currentBeneficiaries < schemes[_schemeId].maxBeneficiaries,
            "Scheme is full"
        );

        schemes[_schemeId].beneficiaries[_applicant] = true;
        schemes[_schemeId].currentBeneficiaries++;
        emit ApplicationApproved(_schemeId, _applicant);
    }

    function rejectApplication(bytes32 _schemeId, address _applicant) external onlyMinistry {
        require(schemes[_schemeId].ministry == msg.sender, "Not the scheme ministry");
        emit ApplicationRejected(_schemeId, _applicant);
    }

    function getSchemeDetails(bytes32 _schemeId)
        external
        view
        returns (
            string memory name,
            string memory description,
            address ministry,
            bool isActive,
            uint256 maxBeneficiaries,
            uint256 currentBeneficiaries
        )
    {
        Scheme storage scheme = schemes[_schemeId];
        return (
            scheme.name,
            scheme.description,
            scheme.ministry,
            scheme.isActive,
            scheme.maxBeneficiaries,
            scheme.currentBeneficiaries
        );
    }

    function getUserApplications(address _user)
        external
        view
        returns (bytes32[] memory)
    {
        return userApplications[_user];
    }

    function isBeneficiary(bytes32 _schemeId, address _user)
        external
        view
        returns (bool)
    {
        return schemes[_schemeId].beneficiaries[_user];
    }
}

contracts/WelfareAudit.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./WelfareToken.sol";

contract WelfareAudit is Ownable {
    WelfareToken public welfareToken;
    mapping(address => bool) public isMinistry;

    struct AuditLog {
        address ministry;
        string program;
        uint256 amount;
        uint256 date;
        string description;
    }

    AuditLog[] public auditLogs;
    mapping(address => uint256[]) public ministryAuditLogs;
    mapping(address => uint256) public ministryAuditLogCount;

    event MinistryRegistered(address indexed ministry);
    event AuditLogCreated(uint256 indexed logId, address indexed ministry);

    constructor(address _welfareToken) Ownable(msg.sender) {
        welfareToken = WelfareToken(_welfareToken);
    }

    function registerMinistry(address ministry) external onlyOwner {
        require(!isMinistry[ministry], "Address is already a ministry");
        isMinistry[ministry] = true;
        emit MinistryRegistered(ministry);
    }

    function createAuditLog(
        string memory program,
        uint256 amount,
        uint256 date,
        string memory description
    ) external {
        require(isMinistry[msg.sender], "Caller is not a ministry");
        require(amount > 0, "Amount must be greater than zero");

        uint256 logId = auditLogs.length;
        auditLogs.push(AuditLog({
            ministry: msg.sender,
            program: program,
            amount: amount,
            date: date,
            description: description
        }));

        ministryAuditLogs[msg.sender].push(logId);
        ministryAuditLogCount[msg.sender]++;

        emit AuditLogCreated(logId, msg.sender);
    }

    function getAuditLog(uint256 index) external view returns (AuditLog memory) {
        require(index < auditLogs.length, "Invalid audit log index");
        return auditLogs[index];
    }

    function getAuditLogCount() external view returns (uint256) {
        return auditLogs.length;
    }

    function getMinistryAuditLog(address ministry, uint256 index) external view returns (AuditLog memory) {
        require(index < ministryAuditLogCount[ministry], "Invalid ministry audit log index");
        return auditLogs[ministryAuditLogs[ministry][index]];
    }

    function getMinistryAuditLogCount(address ministry) external view returns (uint256) {
        return ministryAuditLogCount[ministry];
    }
}

